const { Component } = require('react');
import { nanoid } from 'nanoid';

export class App extends Component {
  state = {
    contacts: [],
    name: '',
  };

  handleChange = e => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const { contacts, number, name } = this.state;
    const newContact = { id: nanoid(), name, number };
    console.log('number', number);

    this.setState(() => {
      return { contacts: [...this.state.contacts, newContact] };
    });

    this.reset();
  };

  reset = () => {
    this.setState({ name: '', number: '' });
  };

  // createContact = () => {
  //   const { contacts } = this.state;
  //   contacts.map((item) => )
  // }

  render() {
    const { contacts, name, number } = this.state;
    return (
      <div>
        <div
          action=""
          className="phonebook"
          style={{
            display: 'flex',
            width: '400px',
            flexDirection: 'column',
            gap: '20px',
            margin: '20px auto',
            fontSize: 30,
            color: '#010101',
            listStyle: 'none',
          }}
        >
          <h1 className="phonebookTitle">Phonebook</h1>
          <form onSubmit={this.handleSubmit} className="phonebook">
            <label htmlFor="">Name</label>
            <input
              onChange={this.handleChange}
              type="text"
              className="username"
              name="name"
              value={this.state.name}
            />
            <label htmlFor="">Number</label>
            <input
              onChange={this.handleChange}
              type="tel"
              name="number"
              value={this.state.number}
              // value="number"
              pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
              title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
              required
            />
            <button className="addContact">Add contact</button>
          </form>
        </div>
        <div
          style={{
            height: 'auto',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            margin: '20px auto',
            fontSize: 30,
            color: '#010101',
            listStyle: 'none',
          }}
        >
          <h1 className="contactTitle">Contacts</h1>
          <label htmlFor="">Find contacts by name</label>
          <input type="text" className="username" />
          <ul className="contactList">
            {/* {console.log(this.state.contacts)} */}
            {console.log(contacts)}
            <li className="contactItem">
              {contacts.name} - {contacts.number}
            </li>
            <li className="contactItem">Name2</li>
            <li className="contactItem">Name3</li>
          </ul>
        </div>
      </div>
    );
  }
}
