import { nanoid } from 'nanoid';
import { Filter } from './Filter/Filter';
const { Component } = require('react');

export class App extends Component {
  state = {
    contacts: [],
    name: '',
  };

  handleChange = e => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const { number, name } = this.state;
    const repeatName = this.state.contacts.find(item => {
      return item.name === name;
    });
    if (repeatName) {
      alert(`${name} is already in your contacts!`);
      this.reset();
      return;
    } else {
      const newContact = { id: nanoid(), name, number };
      this.setState(() => {
        return { contacts: [...this.state.contacts, newContact] };
      });
    }
    this.reset();
  };

  reset = () => {
    this.setState({ name: '', number: '' });
  };

  handleFilterChange = e => {
    const { value } = e.target;
    this.setState({ filter: value });
  };

  render() {
    const { contacts } = this.state;
    const filterContacts = contacts.filter(contact =>
      contact.name
        .toLowerCase()
        .trim()
        .includes(this.state.filter.toLowerCase())
    );
    return (
      <div>
        <div action="" className="phonebook">
          <h1 className="phonebookTitle">Phonebook</h1>
          <form onSubmit={this.handleSubmit} className="phonebook">
            <label htmlFor="">
              Name
              <input
                onChange={this.handleChange}
                type="text"
                className="username"
                name="name"
                value={this.state.name}
                required
              />
            </label>
            <label htmlFor="">
              Number
              <input
                onChange={this.handleChange}
                type="tel"
                name="number"
                value={this.state.number}
                pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
                title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
                required
              />
            </label>
            <button className="addContact">Add contact</button>
          </form>
        </div>
        <div>
          <Filter
            value={this.state.filter}
            onChange={this.handleFilterChange}
          />
          <ul className="contactList">
            {contacts.map(item => (
              <li className="contactItem" key={item.id}>
                {item.name}: <span>{item.number}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}
