const { Component } = require('react');

export class App extends Component {
  state = {
    contacts: [],
    name: '',
  };

  handleSubmit = e => {
    e.preventDefault();
    const form = e.currentTarget;
    const name = form.elements.username.value;
    // const number = form.elements.number;
    console.log(form);

    console.log(name);

    // this.props.onSubmit({ name, number });
    // form.reset();

    this.setState({ contacts: [], name: { name } }, console.log(this.state));
  };

  render() {
    return (
      <div>
        <div
          action=""
          className="phonebook"
          style={{
            display: 'flex',
            width: '400px',
            flexDirection: 'column',
            gap: '20px',
            margin: '20px auto',
            fontSize: 30,
            color: '#010101',
            listStyle: 'none',
          }}
        >
          <h1 className="phonebookTitle">Phonebook</h1>
          <form onSubmit={this.handleSubmit} className="phonebook">
            <label for="">Name</label>
            <input type="text" className="username" name="username" />
            <label for="">Number</label>
            <input
              type="tel"
              name="number"
              pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
              title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
              required
            />
            <button className="addContact">Add contact</button>
          </form>
        </div>
        <div
          style={{
            height: 'auto',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            margin: '20px auto',
            fontSize: 30,
            color: '#010101',
            listStyle: 'none',
          }}
        >
          <h1 className="contactTitle">Contacts</h1>
          <label for="">Find contacts by name</label>
          <input type="text" className="username" />
          <ul className="contactList">
            <li className="contactItem">Name1</li>
            <li className="contactItem">Name2</li>
            <li className="contactItem">Name3</li>
          </ul>
        </div>
      </div>
    );
  }
}
