import PropTypes from 'prop-types';
import { Component } from 'react';
import css from './ContactForm.module.css';

export class ContactForm extends Component {
  // ({ valueNumber, value })
  state = {
    contacts: [],
    name: '',
  };

  handleChange = e => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const { number, name } = this.state;
    const repeatName = this.state.contacts.find(item => {
      return item.name === name;
    });
    if (repeatName) {
      alert(`${name} is already in your contacts!`);
      this.reset();
      return;
    } else {
      const newContact = { id: nanoid(), name, number };
      this.setState(() => {
        return { contacts: [...this.state.contacts, newContact] };
      });
    }
    this.reset();
  };

  reset = () => {
    this.setState({ name: '', number: '' });
  };

  render() {
    return (
      <form onSubmit={handleSubmit} className={css.phonebook}>
        <label className={css.name} htmlFor="">
          Name
          <input
            onChange={handleChange}
            type="text"
            className={css.userName}
            name="name"
            value={this.state.name}
            required
          />
        </label>
        <label className={css.number} htmlFor="">
          Number
          <input
            onChange={handleChange}
            type="tel"
            className={css.userNumber}
            name="number"
            value={this.state.number}
            pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
            title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
            required
          />
        </label>
        <button className={css.addContact}>Add contact</button>
      </form>
    );
  }
}

ContactForm.propTypes = {
  onChange: PropTypes.func.isRequired,
  onSubmit: PropTypes.func.isRequired,
  valueNumber: PropTypes.string,
  value: PropTypes.string.isRequired,
};
