import { nanoid } from 'nanoid';
import { ContactList } from './ContactList/ContactList';
import { Filter } from './Filter/Filter';
const { Component } = require('react');

export class App extends Component {
  state = {
    contacts: [
      { id: 'id-1', name: 'Rosie Simpson', number: '459-12-56' },
      { id: 'id-2', name: 'Hermione Kline', number: '443-89-12' },
      { id: 'id-3', name: 'Eden Clements', number: '645-17-79' },
      { id: 'id-4', name: 'Annie Copeland', number: '227-91-26' },
    ],
    filter: '',
    name: '',
    number: '',
  };

  handleChange = e => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const { number, name } = this.state;
    const repeatName = this.state.contacts.find(item => {
      return item.name === name;
    });
    if (repeatName) {
      alert(`${name} is already in your contacts!`);
      this.reset();
      return;
    } else {
      const newContact = { id: nanoid(), name, number };
      this.setState(() => {
        return { contacts: [...this.state.contacts, newContact] };
      });
    }
    this.reset();
  };

  reset = () => {
    this.setState({ name: '', number: '' });
  };

  onBtnDelete = e => {
    this.setState(prevState => {
      return {
        contacts: prevState.contacts.filter(
          contact => contact.id !== e.target.id
        ),
      };
    });
  };

  handleFilter = e => {
    const { value } = e.target;
    this.setState({ filter: value });
  };

  render() {
    const { contacts } = this.state;
    const filterContact = contacts.filter(contact =>
      contact.name
        .toLowerCase()
        .trim()
        .includes(this.state.filter.toLowerCase())
    );

    return (
      <div>
        <div action="" className="phonebook">
          <h1 className="phonebookTitle">Phonebook</h1>
        </div>
        <div>
          <h1 className="contactTitle">Contacts</h1>
          <Filter onChange={this.handleFilter} value={this.state.filter} />
          <ContactList
            contacts={filterContact}
            onBtnDelete={this.onBtnDelete}
          />
        </div>
      </div>
    );
  }
}
