import { nanoid } from 'nanoid';
import { ContactList } from './ContactList/ContactList';
import { Filter } from './Filter/Filter';
const { Component } = require('react');

export class App extends Component {
  state = {
    contacts: [
      { id: 'id-1', name: 'Rosie Simpson', number: '459-12-56' },
      { id: 'id-2', name: 'Hermione Kline', number: '443-89-12' },
      { id: 'id-3', name: 'Eden Clements', number: '645-17-79' },
      { id: 'id-4', name: 'Annie Copeland', number: '227-91-26' },
    ],
    filter: '',
    name: '',
    number: '',
  };

  handleChange = e => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleSubmit = e => {
    e.preventDefault();
    const { number, name } = this.state;
    const repeatName = this.state.contacts.find(item => {
      return item.name === name;
    });
    if (repeatName) {
      alert(`${name} is already in your contacts!`);
      this.reset();
      return;
    } else {
      const newContact = { id: nanoid(), name, number };
      this.setState(() => {
        return { contacts: [...this.state.contacts, newContact] };
      });
    }
    this.reset();
  };

  reset = () => {
    this.setState({ name: '', number: '' });
  };

  onBtnDelete = e => {
    this.setState(prevState => {
      return {
        contacts: prevState.contacts.filter(
          contact => contact.id !== e.target.id
        ),
      };
    });
  };

  handleFilter = e => {
    const { value } = e.target.value;
  };

  render() {
    const { contacts } = this.state;
    return (
      <div>
        <div action="" className="phonebook">
          <h1 className="phonebookTitle">Phonebook</h1>
          <form onSubmit={this.handleSubmit} className="phonebook">
            <label htmlFor="">
              Name
              <input
                onChange={this.handleChange}
                type="text"
                className="username"
                name="name"
                value={this.state.name || ''}
                required
              />
            </label>
            <label htmlFor="">
              Number
              <input
                onChange={this.handleChange}
                type="tel"
                name="number"
                value={this.state.number}
                pattern="\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}"
                title="Phone number must be digits and can contain spaces, dashes, parentheses and can start with +"
                required
              />
            </label>
            <button className="addContact">Add contact</button>
          </form>
        </div>
        <div>
          <h1 className="contactTitle">Contacts</h1>
          <Filter onChange={this.handleFilter} value={this.state.filter} />
          <ContactList contacts={contacts} onBtnDelete={this.onBtnDelete} />
        </div>
      </div>
    );
  }
}
